"-- AdMob Configuration Verification Script -- -- This script checks if the AdMob configuration is properly set up in the database -- SELECT '=== ADMOB CONFIGURATION VERIFICATION ===' as verification_status; -- Check if all required AdMob keys exist SELECT COUNT(*) as total_admob_keys FROM app_config WHERE config_key LIKE 'admob%'; -- Check for missing values SELECT config_key, 'MISSING_VALUE' as status FROM app_config WHERE config_key LIKE 'admob%' AND (config_value IS NULL OR config_value = ''); -- Check for test IDs (should be production IDs) SELECT config_key, config_value, 'TEST_ID_DETECTED' as status FROM app_config WHERE config_key LIKE 'admob%' AND config_value LIKE 'ca-app-pub-3940256099942544%'; -- Check for production IDs SELECT config_key, config_value, 'PRODUCTION_ID' as status FROM app_config WHERE config_key LIKE 'admob%' AND config_value LIKE 'ca-app-pub-3794036444002573%'; -- Summary SELECT '=== CONFIGURATION SUMMARY ===' as summary; SELECT CASE WHEN COUNT(*) = 8 THEN 'ALL_8_ADMOB_KEYS_PRESENT' ELSE CONCAT('MISSING_KEYS: ', 8 - COUNT(*), ' keys missing') END as admob_keys_status FROM app_config WHERE config_key LIKE 'admob%'; SELECT CASE WHEN COUNT(*) = 0 THEN 'NO_MISSING_VALUES' ELSE CONCAT('MISSING_VALUES: ', COUNT(*), ' keys have no values') END as missing_values_status FROM app_config WHERE config_key LIKE 'admob%' AND (config_value IS NULL OR config_value = ''); SELECT CASE WHEN COUNT(*) = 0 THEN 'NO_TEST_IDS' ELSE CONCAT('TEST_IDS_FOUND: ', COUNT(*), ' keys still have test IDs') END as test_ids_status FROM app_config WHERE config_key LIKE 'admob%' AND config_value LIKE 'ca-app-pub-3940256099942544%'; SELECT CASE WHEN COUNT(*) >= 6 THEN 'PRODUCTION_IDS_READY' ELSE CONCAT('PRODUCTION_IDS_MISSING: ', 6 - COUNT(*), ' production IDs missing') END as production_ids_status FROM app_config WHERE config_key LIKE 'admob%' AND config_value LIKE 'ca-app-pub-3794036444002573%';" 
